package Implement_OOPS_using_java;
import java.util.Scanner;

public class Class_02_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner ScrObj = new Scanner(System.in);
		System.out.println("enter any value");
		int n = ScrObj.nextInt();
		System.out.println("given value is  " + n);
		System.out.println("square value is  " + (n*n));
		System.out.println("Cube value is  " + (n*n*n));
	}

}
